import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import '../../Css/Productlar.css';
import Navbar from '../Navbar';
import Footer from '../Footer';

class Productlar extends Component {
    static contextType = YourContext; // Burada YourContext, context'inizi sağlayan Context API'niz olmalı

    render() {
        const { products } = this.context; // this.context'ten addCart ihtiyacınız olmadığı için kaldırdım

        return (
            <div>
                <Navbar />
                <div id="product">
                    {
                        products.map(product => (
                            <div className="card" key={product._id}>
                                <Link to={`/product/${product._id}`}>
                                    <img src={product.src} alt="" />
                                </Link>
                                <div className="content">
                                    <h3>
                                        <Link to={`/product/${product._id}`}>{product.title}</Link>
                                    </h3>
                                    <p>{product.description}</p>
                                    <span>{product.price} TL</span>
                                </div>
                            </div>
                        ))
                    }
                </div>
                <Footer />
            </div>
        );
    }
}

export default Productlar;
