import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, useParams } from 'react-router-dom';
import axios from 'axios';

// Sepet bileşeni
const Cart = ({ cart, products }) => {
  return (
    <div>
      <h2>Sepet</h2>
      <ul>
        {cart.map((productId, index) => {
          const product = products.find((p) => p.id === productId);
          return <li key={index}>{product?.name} - {product?.price}₺</li>;
        })}
      </ul>
    </div>
  );
};

// Ürünler bileşeni
const Products = ({ products, addToCart }) => {
  return (
    <div>
      <h1>Ürünler</h1>
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            {product.name} - {product.price}₺
            <button onClick={() => addToCart(product.id)}>Sepete Ekle</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

// Ana bileşen
const App = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [notification, setNotification] = useState('');

  // Ürünleri veritabanından çekme
  useEffect(() => {
    axios.get('/api/products') // API endpoint'inizi buraya yazın
      .then(response => {
        setProducts(response.data);
      })
      .catch(error => {
        console.error('Ürünler yüklenirken bir hata oluştu:', error);
      });
  }, []);

  // Ürünü sepete ekleme fonksiyonu
  const addToCart = (productId) => {
    setCart([...cart, productId]);
    showNotification('Ürün sepete eklendi!');
  };

  // Bildirim gösterme fonksiyonu
  const showNotification = (message) => {
    setNotification(message);
    setTimeout(() => {
      setNotification('');
    }, 2000); // Bildirim 2 saniye sonra kaybolur
  };

  return (
    <Router>
      <div>
        {notification && <div className="notification">{notification}</div>}
        <Switch>
          <Route path="/cart">
            <Cart cart={cart} products={products} />
          </Route>
          <Route path="/">
            <Products products={products} addToCart={addToCart} />
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
