const Basket = ({ cart }) => {
    const cartLength = cart?.length || 0;  // Use optional chaining and default to 0
  
    return (
      <div className="basket-container">
        <h2>Sepetiniz</h2>
        {cartLength === 0 ? (
          <p>Sepetinizde ürün bulunmamaktadır.</p>
        ) : (
          <ul>
            {cart.map((id) => (
              <li key={id}>{id}</li>
            ))}
          </ul>
        )}
      </div>
    );
  };
