import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../../Css/Login.css';

function LoginModal({ onClose, show, toggleModal }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    const user = { username, password };
    const response = await fetch('/api/login', {
      method: 'POST',
      body: JSON.stringify(user),
      headers: {
        'Content-Type': 'application/json',
      },
    });

    const json = await response.json();
    if (!response.ok) {
      setError(json.error);
    } else {
      setError(null);
      setUsername('');
      setPassword('');
      setSuccessMessage('Giriş başarılı! Hoş geldiniz.');
      console.log('user logged in', json);
      // You can add additional logic here to redirect the user or close the modal
    }
  };

  return (
    <div className={`login-modal ${show ? 'show' : ''}`}>
      <div className="login-modal-content">
        <span className="close" onClick={onClose}>&times;</span>
        <h3 style={{ width: '100px', marginLeft: '30px', color: 'black', float: 'left', fontFamily: 'Font1', marginTop: '-25px' }}>Üyelik</h3>
        <br></br>

        {/* Giriş yap formu */}
        <form onSubmit={handleLogin}>
          {/* Kullanıcı adı */}
          <label htmlFor="username" className="labelgiriş">E-posta</label>
          <input
            type="text"
            id="username"
            name="username"
            className="input"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />

          {/* Şifre */}
          <label htmlFor="password" className="labelgiriş">Şifre</label>
          <input
            type="password"
            id="password"
            name="password"
            className="input"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />

          {/* "Beni Hatırla" metni */}
          <input type="checkbox" id="rememberMe" className="irememberMe" />
          <label htmlFor="rememberMe" className="irememberLabel">Beni Hatırla</label>

          <a href="#" className="şifreyiunuttum">Şifremi Unuttum</a>
          <input type="submit" value="Giriş Yap" className="login-button" />

          {error && <div className="error">{error}</div>}
          {successMessage && <div className="success">{successMessage}</div>}
        </form>
        <br />

        <div className="button-container">
          {/* Facebook ile giriş */}
          <div className="square-button">
            <button>Facebook ile Giriş</button>
          </div>
          {/* Google ile giriş */}
          <div className="square-button">
            <button>Google ile Giriş</button>
          </div>
          {/* Üye ol */}
          <div className="square-button">
            <Link to="/login">Üye ol</Link>
          </div>
        </div>
      </div>
      <div className="login-modal-background" onClick={onClose} />
    </div>
  );
}

export default LoginModal;
