// Details.js

import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import '../../Css/Details.css';
import Navbar from '../../components/Navbar';
import Footer from '../../components/Footer';
import Accordion from './Accordion';
import ImageSlider from './ImageSlider';
import Basket from './Basket'; // Basket bileşenini import ediyoruz

const Details = () => {
    const { id } = useParams();
    const [product, setProduct] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [cart, setCart] = useState([]);
    const [notification, setNotification] = useState(null);

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const response = await fetch(`/api/products/${id}`);
                if (!response.ok) {
                    throw new Error('Ürün bulunamadı.');
                }
                const json = await response.json();
                setProduct(json);
            } catch (error) {
                console.error('Ürün getirme hatası:', error);
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchProduct();
    }, [id]);

    const addToCart = () => {
        setCart(prevCart => {
            const updatedCart = [...prevCart, id];
            console.log("Sepetteki Ürünler:", updatedCart);
            return updatedCart;
        });
        setNotification('Ürün sepete eklendi');
        setTimeout(() => setNotification(null), 3000);
    };

    const data = [
        // Accordion data...
    ];

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;
    if (!product) return <div>Product not found</div>;

    return (
        <>
            <Navbar />
            <div className="product-details-container">
                <div className="details">
                    <div className="image-container">
                        <img
                            src={`/${product.image}`}
                            alt={product.title}
                            onError={(e) => {
                                console.error("Error loading image:", e.target.src);
                                e.target.onerror = null;
                                e.target.src = "/images/fallback-image.jpg";
                            }}
                        />
                    </div>
                    <div className="info-container">
                        <h1>{product.title}</h1>
                        <p className="description">{product.description}</p>
                        <p className="label">Beden</p>
                        <div className="sizes">
                            <span>S</span>
                            <span>M</span>
                            <span>L</span>
                            <span>XL</span>
                            <span>2XL</span>
                        </div>
                        <p className="label">Renk</p>
                        <div className='colors'>
                            {product.color}
                            <div className='outer-circle' style={{ borderColor: product.color }}>
                                <div className='inner-circle' style={{ backgroundColor: product.color }}></div>
                            </div>
                        </div>
                        <div className="price-and-cart">
                            <p className="price">{product.price} TL</p>
                            <button className="add-to-cart-btn" onClick={addToCart}>
                                Sepete Ekle
                            </button>
                            {notification && <div className="notification">{notification}</div>}
                        </div>
                    </div>
                </div>
            </div>
            <Accordion items={data} keepOthersOpen={false} />
            <Basket cart={cart} /> {/* Sepet bileşenini kullanarak sepet içeriğini gösteriyoruz */}
            <React.StrictMode>
                <ImageSlider />
            </React.StrictMode>
            <Footer />
        </>
    );
};

export default Details;
