import React, { useState } from 'react';
import Navbar from '../../components/Navbar';
import "../../Css/Login.css";
import girişyaplogo from '../../images/girişyaplogo.jpg.png';
import Footer from '../../components/Footer';
import { Link } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const customer = { email, password };
    const response = await fetch('/api/customers/login', {
      method: 'POST',
      body: JSON.stringify(customer),
      headers: {
        'Content-Type': 'application/json',
      },
    });
    const json = await response.json();
    if (!response.ok) {
      setError(json.error);
    } else {
      setError(null);
      setEmail('');
      setPassword('');
      setSuccessMessage('Giriş başarılı! Hoş geldiniz.');
      console.log('customer logged in', json);
    }
  };

  return (
    <div>
      <Navbar />
      <div className="bg section s9">
        <div className="Hoşgeldin">
          <br /><br />
          HOŞ GELDİN
        </div>

        <div className="wrapper">
          <div className="item">
            <h3>Giriş Yap</h3><br />
            <form onSubmit={handleSubmit}>
              <label htmlFor="email" className="giriş-yazı">E-posta Adresin</label>
              <input
                type="email"
                name="email"
                onChange={(e) => setEmail(e.target.value)}
                value={email}
              />
              <label htmlFor="password" className="giriş-yazı">Şifren</label>
              <input
                type="password"
                name="password"
                onChange={(e) => setPassword(e.target.value)}
                value={password}
              />
              <button className='kaftaüyeol' type="submit">Giriş Yap</button>
              {error && <div className="error">{error}</div>}
              {successMessage && <div className="success">{successMessage}</div>}
            </form>
            <Link to="/signup">
              <a className="girişyapbutton">Üye Ol</a><br /><br />
            </Link>
            <span className="üyeliğimvar">Daha önceden üyeliğim yok</span>
          </div>

          {/* Diğer kodlarınız burada devam eder... */}
        </div>
        <div style={{ display: 'block', width: '100%', height: '20vh' }}></div>
      </div>
      <Footer />
    </div>
  );
};

export default Login;
