import React from 'react';
import { Link } from 'react-router-dom';
import '../../Css/loginNew.css';

function LoginModal({ onClose, show }) {
  if (!show) {
    return null;
  }

  return (
    <div className="login-modal">
      <div className="login-modal-content">
        <span className="close" onClick={onClose}>&times;</span>
        <h3 className="modal-header">Üyelik</h3>

        {/* Giriş yap formu */}
        <form>
          {/* E-posta */}
          <label htmlFor="email" className="label-giris">E-posta</label>
          <input type="email" id="email" name="email" className="input" required />

          {/* Şifre */}
          <label htmlFor="password" className="label-giris">Şifre</label>
          <input type="password" id="password" name="password" className="input" required />

          {/* "Beni Hatırla" metni */}
          <div className="remember-forgot">
            <label>
              <input type="checkbox" id="rememberMe" className="remember-me" /> Beni Hatırla
            </label>
            <a href="#" className="forgot-password">Şifremi Unuttum</a>
          </div>

          <button type="submit" className="login-button">Giriş Yap</button>
        </form>

        <div className="or">Veya</div>

        <div className="button-container">
          {/* Facebook ile giriş */}
          <button className="square-button facebook-btn">
            Facebook ile Giriş
          </button>
          {/* Google ile giriş */}
          <button className="square-button google-btn">
            Google ile Giriş
          </button>
          {/* Üye ol */}
          <button className="square-button register-btn">
            <Link to="/register">Üye Ol</Link>
          </button>
        </div>
      </div>
      <div className="login-modal-background" onClick={onClose} />
    </div>
  );
}

export default LoginModal;
