import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import '../../Css/resetPassword.css';
import Navbar from '../Navbar';

function PasswordResetRequest() {
  const [email, setEmail] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const enteredEmail = email.trim();

    try {
      const response = await fetch('/api/resetpassword/check', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: enteredEmail }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Email is not found');
      }

      const data = await response.json();
      console.log('Email is found:', data);

      setSuccessMessage(`Hoş geldin, ${data.customerName}!`); // Veri yapısına göre ismi kontrol edin
      setErrorMessage('');

    } catch (error) {
      console.error('Password reset request failed:', error);
      setSuccessMessage(null);
      setErrorMessage('Email bulunamadı. Lütfen geçerli bir email adresi girin.');
    }
  };

  return (
    <div>
      <Navbar />
      <div className='body'>
        <div className="form" >
          <div className="title">Welcome</div>
          <div className="subtitle">Please write your email</div>
          <form onSubmit={handleSubmit}>
            <div className="input-container ic1">
              <input
                id="email"
                className="input"
                type="email"
                placeholder=" "
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <div className="cut cut-short"></div>
              <label htmlFor="email" className="placeholder">
                Email
              </label>
            </div>
            <button type="submit" className="submit">
              Submit
            </button>
            {errorMessage && <div className="error-message">{errorMessage}</div>}
            {successMessage && <div className="success-message">{successMessage}</div>}
            <a href='/' style={{ color: 'white' }}>Back to home</a>
          </form>
        </div>
      </div>
    </div>
  );
}

export default PasswordResetRequest;
