// LoginModal.js
import React from 'react';

function LoginModal({ onClose }) {
  const handleSubmit = async (e) => {
    e.preventDefault();
    const email = e.target.elements.email.value;
    const password = e.target.elements.password.value;

    try {
      const response = await fetch('/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        throw new Error('Giriş başarısız oldu.');
      }

      const data = await response.json();
      console.log('Kullanıcı girişi başarılı:', data);

      // Burada başarılı giriş sonrası işlemleri gerçekleştirebilirsiniz,
      // örneğin kullanıcıyı ana sayfaya yönlendirebilirsiniz.

    } catch (error) {
      console.error('Giriş hatası:', error.message);
      // Hata durumunda kullanıcıya bir hata mesajı gösterebilirsiniz.
    }
  };

  return (
    <div className="login-modal">
      <div className="login-modal-content">
        <span className="close" onClick={onClose}>&times;</span>
        <h3 style={{ width: '100px', marginLeft: '30px', color: 'black', float: 'left', fontFamily: 'Font1', marginTop: '-25px' }}>Üyelik</h3>
        <br></br>
        
        {/* Giriş yap formu */}
        <form onSubmit={handleSubmit}>
          {/* Kullanıcı adı */}
          <label htmlFor="username" className="labelgiriş">E-posta</label>
          <input type="text" id="email" name="email" className="input" />

          {/* Şifre */}
          <label htmlFor="password" className="labelgiriş">Şifre</label>
          <input type="password" id="password" name="password" className="input" />

          {/* "Beni Hatırla" metni */}
          <input type="checkbox" id="rememberMe" className="irememberMe" />
          <label htmlFor="rememberMe" className="irememberLabel">Beni Hatırla</label>

          <a href="#" className="şifreyiunuttum">Şifremi Unuttum</a>
          {/* login butonu */}
          <input type="submit" value="Giriş Yap" className="login-button" /> 
        </form>
        <br />

        <div className="button-container">
          {/* Facebook ile giriş */}
          <div className="square-button">
            <button>Facebook ile Giriş</button>
          </div>
          {/* Google ile giriş */}
          <div className="square-button">
            <button>Google ile Giriş</button>
          </div>
          {/* Üye ol */}
          <div className="square-button">
            <a href="/signup">Üye Ol</a>
          </div>
        </div>
      </div>
      <div className="login-modal-background" onClick={onClose} />
    </div>
  );
}

export default LoginModal;
