// Basket.js

import React from 'react';

const Basket = ({ cart }) => {
    return (
        <div className="basket-container">
            <h2>Sepetinizdeki Ürünler</h2>
            <ul>
                {cart.map(productId => (
                    <li key={productId}>
                        Ürün ID: {productId} {/* Burada ürün detayları da gösterilebilir */}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Basket;
