import { Link } from 'react-router-dom';
import '../../Css/Productlar.css';
import Navbar from '../Navbar';
import Footer from '../Footer';


const Productlar = () => {
    const [products, setProducts] = useState([])

    useEffect(() => {
        const fetchProducts = async () => {
            const response = await fetch('/api/products')
            const json = await response.json()

            if (response.ok) {
                setProducts(json)
            }
        }

        fetchProducts()
    }, [])

    return (
        <div>
            <Navbar />
            <div id="product">
                
                 {products && products.map((product) => (
                    <tr key={product._id}>
                        <td>{product._id}</td>
                        <td>{product.title}</td>
                        <td>
                            {product.image && <img src={`http://localhost:4000/${product.image}`} alt="Product Image" style={{ maxWidth: '100px' }} />}
                        </td>
                        <td>{product.description}</td>
                        <td>{product.content}</td>
                        <td>{product.price}</td>
                        <td>{product.color}</td>
                        <td>{product.count}</td>
                        <td>
                            <Link to={`/admin/update/customer/${product._id}`}>
                                <button className="button" role="button">
                                    Update
                                </button>
                            </Link>
                        </td>
                        <td>
                            <button className="buttonDelete" onClick={() => handleClick(product._id)}>
                                Delete
                            </button>
                        </td>
                    </tr>
                ))}
                 


                    products.map(product => (
                        <div className="card" key={product._id}>
                            <Link to={`/product/${product._id}`}>
                                <img src={product.src} alt="" />
                            </Link>
                            <div className="content">
                                <h3>
                                    <Link to={`/product/${product._id}`}>{product.title}</Link>
                                </h3>
                                <p>{product.description}</p>
                                <span>{product.price} TL</span>
                            </div>
                        </div>
                    ))
                }
            </div>
            <Footer />
        </div>
    );
}

export default Productlar;
