import { BrowserRouter, Routes, Route } from 'react-router-dom';

// pages & components
import Home from './pages/HomePage';
import AdminPage from './pages/AdminPage';
import AddCustomer from './components/AdminPage/AddCustomer'
import Singup from './components/HomePage/Login'
import ContactPage from './pages/ContactPage';
import KampanyaliUrunler from './pages/KampanyaliUrunler';
import GiftCard from './components/HomePage/GiftCard';
import PasswordReset from './components/HomePage/PasswordResetRequest';
import Productlar from './components/Products/Productlar';
import Details from './components/Products/Details';
import Cart from './components/HomePage/Cart';
import { DataProvider } from './components/Products/Context';
import AddProduct from './components/Products/AddProduct';

function App() {
  return (
    <div className="App">
      <DataProvider>
        <BrowserRouter>
          <div className="pages">
            <Routes>
              <Route
                path="/"
                element={<Home />}
              />
              <Route
                path="/admin"
                element={
                  <AdminPage />
                }
              />
              <Route
                path="/singup"
                element={
                  <Singup />
                }
              />
              <Route
                path="/admin/addcustomer"
                element={
                  <AddCustomer />
                }
              />
              <Route
                path="/iletisim"
                element={
                  <ContactPage />
                }
              />
              <Route
                path="/kampanyaliurunler"
                element={
                  <KampanyaliUrunler />
                }
              />
              <Route
                path="/GiftCard"
                element={
                  <GiftCard />
                }
              />
               <Route
                path="/AddProduct"
                element={
                  <AddProduct />
                }
              />
              <Route path="/product" element={<Productlar />} />
              <Route path="/product/:id" element={<Details />} />
              <Route path="/cart" element={<Cart />} />

              <Route
                path="/PasswordReset"
                element={
                  <PasswordReset />
                }
              />
            </Routes>
          </div>
        </BrowserRouter>
      </DataProvider>
    </div>
  );
}

export default App;
