import { useEffect, useState } from "react"
import { Link } from 'react-router-dom'; // Link componentini ekleyin

import formatDistanceToNow from 'date-fns/formatDistanceToNow'
const Titles = {
    id: 'Product ID',
    title: 'Title',
    image: 'Ä°mage',
    description: 'Description',
    price: 'Price',
    color: 'Color',
    count: 'Count',
    update:'Update',
    delete:'Delete',
};

const Home = () => {
    const [products, setProducts] = useState([])

    useEffect(() => {
        const fetchProducts = async () => {
            const response = await fetch('/api/products')
            const json = await response.json()

            if (response.ok) {
                setProducts(json)
            }
        }

        fetchProducts()
    }, [])

    const handleClick = async (id) => {
        const response = await fetch('/api/products/' + id, {
            method: 'DELETE'
        })
        const json = await response.json()
        if (response.ok) {
            setProducts(customers.filter(customer => customer._id !== id))
        }
    }

    return (
        <div className="home">

            <div style={{ margin: '10%' }}>
                <h1>Admin Panel</h1>
                <h2>Customers List</h2>
                <div>
                    <h4 style={{ display: 'inline-block', marginRight: '10px' }}>
                        Add New Customer:
                    </h4>
                    <Link to="/admin/addcustomer">
                        <button className="button" role="button">
                            Add Customer
                        </button>
                    </Link>
                </div>
                <table>
                    <thead>
                        <tr>
                            {Object.values(Titles).map((title) => (
                                <th key={title}>
                                    <h3>{title}</h3>
                                </th>
                            ))}
                        </tr>
                    </thead>
                    <tbody>
                        {customers && customers.map((customer) => (
                            <tr key={customer._id}>
                                <td>{customer._id}</td>
                                <td>{customer.name}</td>
                                <td>{customer.surname}</td>
                                <td>{customer.email}</td>
                                <td>{formatDistanceToNow(new Date(customer.createdAt), { addSuffix: true })}</td>
                                <td>
                                    <Link to={`/admin/update/customer/${customer._id}`}>
                                        <button className="button" role="button">
                                            Update
                                        </button>
                                    </Link>
                                </td>
                                <td>
                                    <button className="buttonDelete" onClick={() => handleClick(customer._id)}>
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default Home
